return {-- Examples of Greek letter snippets, autotriggered for efficiencys({trig=";a", snippetType="autosnippet"},  {    t("\\alpha"),  }),s({trig=";b", snippetType="autosnippet"},  {    t("\\beta"),  }),s({trig=";g", snippetType="autosnippet"},  {    t("\\gamma"),  }),-- Combining text and insert nodes to create basic LaTeX commandss({trig="tt", dscr="Expands 'tt' into '\texttt{}'"},  {    t("\\texttt{"), -- remember: backslashes need to be escaped    i(1),    t("}"),  }),-- Yes, these jumbles of text nodes and insert nodes get messy fast, and yes,-- there is a much better, human-readable solution: ls.fmt, described shortly.s({trig="ff", dscr="Expands 'ff' into '\frac{}{}'"},  {    t("\\frac{"),    i(1),  -- insert node 1    t("}{"),    i(2),  -- insert node 2    t("}")  }),-- Using a zero-index insert node to exit snippet in equation bodys({trig="eq", dscr=""},  fmta(    [[      \begin{equation}          <>      \end{equation}    ]],    { i(0) }  )),-- Code for environment snippet in the above GIFs({trig="env", snippetType="autosnippet"},  fmta(    [[      \begin{<>}          <>      \end{<>}    ]],    {      i(1),      i(2),      rep(1),  -- this node repeats insert node i(1)    }  )),-- Example use of insert node placeholder texts({trig="hr", dscr="The hyperref package's href{}{} command (for url links)"},  fmta(    [[\href{<>}{<>}]],    {      i(1, "url"),      i(2, "display name"),    }  )),}